import React, {useState} from 'react';

const TodoList = () => {
  const [todo, setTodo] = useState({
    description: '',
    date: ''
  });
  const [todos, setTodos] = useState([]);

  const descChanged = (event) => {
    setTodo({...todo, description: event.target.value});
  }

  const dateChanged = (event) => {
    setTodo({...todo, date: event.target.value});
  }


  const addTodo = (event) => {
    event.preventDefault();
    setTodos([...todos, todo]);
    setTodo({description:'', date: ''});

  }

  return (
    <div>
      <form onSubmit={addTodo}>
        <span>Date:</span>
        <input type="date" onChange={dateChanged} value={todo.date}/>
        <span>Description:</span>
        <input type="text" onChange={descChanged} value={todo.description}/>
        <input type="submit" value="Add"/>
      </form>
      <table>
        <tbody>
          <tr><th>Date</th><th>Description</th></tr>
          {todos.map((todo, index) => <tr key={index}><td>{todo}</td></tr>)}
        </tbody>
      </table>
    </div>
  );
};

export default TodoList;
